import { createSlice } from "@reduxjs/toolkit";
import { Home } from "../Home";

const initialState = { home: Home, carpetArea: 510 };

export const HomeSlice = createSlice({
  name: "counter",
  initialState,
  reducers: {
    bedroomIncrement: (state, action) => {
      state.home.push(action.payload);
      state.carpetArea += 120;
    },

    bedroomDecrement: (state, action) => {
      const newArray = state.home.filter((room) => {
        return room.title !== ` Bedroom ${action.payload}`;
      });
      state.home = newArray;

      if (action.payload > 1) {
        state.carpetArea -= 120;
      }
    },

    bathroomIncrement: (state, action) => {
      state.home.push(action.payload);
      state.carpetArea += 40;
    },

    bathroomDecrement: (state, action) => {
      const newArray = state.home.filter((room) => {
        return room.title !== ` Bathroom ${action.payload}`;
      });
      state.home = newArray;

      if (action.payload > 1) {
        state.carpetArea -= 40;
      }
    },
  },
});

export const {
  bedroomIncrement,
  bedroomDecrement,
  bathroomIncrement,
  bathroomDecrement,
} = HomeSlice.actions;

export default HomeSlice.reducer;
