const initialState = 0;

// State: changeBedroom
const changeBathroom = (state = initialState, action) =>{
    switch(action.type){
        case "INCREMENT_BATHROOM": return state + 1;
        case "DECREMENT_BATHROOM": return state - 1;
        default: return state;
    }
}

export default changeBedroom
